import React, { Component } from 'react'
import PropTypes from 'prop-types'
import './new-task-form.css'

export default class NewTaskForm extends Component {
  static defaultProps = {
    onTaskAdd: () => {},
  }

  static propTypes = {
    onTaskAdd: PropTypes.func,
  }

  state = {
    label: '',
    min: '',
    sec: '',
  }

  onLabelChange = (e) => {
    this.setState({
      label: e.target.value,
    })
  }
  onMinChange = (e) => {
    this.setState({
      min: e.target.value,
    })
  }

  onSubmit = (e) => {
    if (e.key === 'Enter') {
      this.props.onTaskAdd(this.state.label)
      this.setState({
        label: '',
      })
    }
  }

  render() {
    const { label, min, sec } = this.state
    return (
      <form className="new-todo-form">
        <input
          value={label}
          className="new-todo"
          placeholder="What needs to be done?"
          autoFocus
          onChange={this.onLabelChange}
          onKeyDown={this.onSubmit}
        />
        <input className="new-todo-form__timer" placeholder="Min" value={min} onChange={this.onMinChange} />
        <input className="new-todo-form__timer" placeholder="Sec" value={sec} onChange={this.onLabelChange} />
      </form>
    )
  }
}
