import React, { Component } from 'react'
import './task-list.css'
import PropTypes from 'prop-types'

import Task from '../task'

export default class TaskList extends Component {
  static defaultProps = {
    onDeleted: () => {},
    onSave: () => {},
    onToggleCompleted: () => {},
    onToggleEditing: () => {},
    filter: 'All',
  }

  static propTypes = {
    todos: PropTypes.arrayOf(PropTypes.object).isRequired,
    filter: PropTypes.string,
    onDeleted: PropTypes.func,
    onSave: PropTypes.func,
    onToggleCompleted: PropTypes.func,
    onToggleEditing: PropTypes.func,
    addTimerId: PropTypes.func,
  }

  filterSort(filter, elementState) {
    let visible = true
    switch (filter) {
      case 'All':
        visible = true
        break
      case 'Active':
        if (!elementState) {
          visible = true
        } else {
          visible = false
        }
        break
      case 'Completed':
        if (elementState) {
          visible = true
        } else {
          visible = false
        }
        break
      default:
        visible = true
    }
    return visible
  }

  render() {
    const { todos, onDeleted, onSave, onToggleCompleted, onToggleEditing, filter, addTimerId } = this.props
    let elements = []

    // function TaskWithProps(item) {
    //   return (
    //     <Task
    //       date={item.date}
    //       label={item.label}
    //       key={item.id}
    //       completed={item.completed}
    //       editing={item.editing}
    //       onDeleted={() => onDeleted(item.id)}
    //       onSave={() => onSave(item.id)}
    //       onToggleCompleted={() => {
    //         onToggleCompleted(item.id)
    //       }}
    //       onToggleEditing={() => {
    //         onToggleEditing(item.id)
    //       }}
    //       timer={item.timer}
    //       addTimerId={addTimerId}
    //       timerId={item.timerId}
    //     />
    //   )
    // }

    elements = todos.map((item) => {
      let visible = this.filterSort(filter, item.completed)

      return (
        <Task
          date={item.date}
          label={item.label}
          key={item.id}
          completed={item.completed}
          editing={item.editing}
          onDeleted={() => onDeleted(item.id)}
          onSave={() => onSave(item.id)}
          onToggleCompleted={() => {
            onToggleCompleted(item.id)
          }}
          onToggleEditing={() => {
            onToggleEditing(item.id)
          }}
          timer={item.timer}
          addTimerId={addTimerId}
          timerId={item.timerId}
          visible={visible}
        />
      )
    })

    // switch (filter) {
    //   case 'Active':
    //     elements = todos.map((item) => {
    //       if (!item.completed) {
    //         return TaskWithProps(item)
    //       }
    //     })
    //     break
    //   case 'Completed':
    //     elements = todos.map((item) => {
    //       if (item.completed) {
    //         return TaskWithProps(item)
    //       }
    //     })
    //     break
    //   case 'All':
    //     elements = todos.map((item) => TaskWithProps(item))
    //     break
    //   default:
    //     throw new Error('Incorrect filter')
    // }

    return <ul className="todo-list">{elements}</ul>
  }
}
