import React, { useState, useEffect } from 'react'
import { formatDistanceToNow } from 'date-fns'
import PropTypes from 'prop-types'
import KG from 'date-fns/locale/en-AU'

import './task.css'

function Task({ onDeleted, onToggleCompleted, onToggleEditing, completed, editing, date, visible, label, timer }) {
  const [newLabel, setLabel] = useState(label)
  const [currentTime, setCurrentTime] = useState('')
  const [futureTime, setFutureTime] = useState('')
  const [timerId, setTimerId] = useState('')

  useEffect(() => {
    updateTimer(timer)
    return () => clearInterval(timerId)
  }, [])

  const updateTimer = (time = futureTime) => {
    if (time < 0) {
      clearInterval(timerId)
      return
    }
    let min = Math.floor(time / 60)
    let seconds = time % 60
    min = min < 10 ? '0' + min : min
    seconds = seconds < 10 ? '0' + seconds : seconds
    let res = `${min}:${seconds}`

    setCurrentTime(res)
    setFutureTime((time = time - 1))
  }

  const onEditingChange = (e) => {
    setLabel(e.target.value)
  }

  const onEditingChangeComplete = (e) => {
    if (e.key === 'Enter') {
      onToggleEditing()
    }
  }

  const timerStart = () => {
    if (timerId) {
      return
    }
    const timerId = setInterval(this.updateTimer, 1000)
    setTimerId(timerId)
  }

  const timerStop = () => {
    clearInterval(timerId)
    setTimerId('')
  }

  let classNames = ''

  if (completed) {
    classNames += 'completed'
  }
  if (editing) {
    classNames += ' editing'
  }

  if (!visible) {
    classNames += ' hidden'
  }

  return (
    <li className={classNames}>
      <div className="view">
        <input className="toggle" type="checkbox" onChange={onToggleCompleted} checked={completed} />
        <label>
          <span className="title" onClick={onToggleCompleted}>
            {newLabel}
          </span>
          <span className="description">
            <button className="icon icon-play" onClick={timerStart}></button>
            <button className="icon icon-pause" onClick={timerStop}></button>
            {currentTime}
          </span>
          <span className="description">
            {`created ${formatDistanceToNow(date, {
              includeSeconds: true,
              locale: KG,
              addSuffix: true,
            })}`}
          </span>
        </label>
        <button className="icon icon-edit" onClick={onToggleEditing} />
        <button className="icon icon-destroy" onClick={onDeleted} />
      </div>
      <input
        type="text"
        className="edit"
        defaultValue={label}
        onChange={onEditingChange}
        onKeyDown={onEditingChangeComplete}
      />
    </li>
  )
}

Task.defaultProps = {
  date: new Date(),
}

Task.propTypes = {
  date: PropTypes.instanceOf(Date),
  completed: PropTypes.bool,
  editing: PropTypes.bool,
  label: PropTypes.string,
  onToggleEditing: PropTypes.func,
  onToggleCompleted: PropTypes.func,
  onDeleted: PropTypes.func,
}

export default Task
